import React, { useState } from 'react'; 
import { useNavigate } from 'react-router-dom';

function LoginPage() {
  const [role, setRole] = useState('student');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [classroomCode, setClassroomCode] = useState('');
  const [isRegistering, setIsRegistering] = useState(false); // Toggle for registration
  const navigate = useNavigate();

  const handleLogin = () => {
    if (!username || !password) {
      alert('Please enter a username and password.');
      return;
    }

    if (role === 'student') {
      if (isRegistering) {
        // Register the student by saving classroom code
        const classrooms = JSON.parse(localStorage.getItem('classrooms')) || [];
        classrooms.push({ code: classroomCode, name: username });
        localStorage.setItem('classrooms', JSON.stringify(classrooms));
        alert('Student registered successfully!');
        navigate('/student', { state: { username } });
      } else {
        // Login logic for existing student
        const savedClassrooms = JSON.parse(localStorage.getItem('classrooms')) || [];
        const classroomExists = savedClassrooms.some(
          (classroom) => classroom.code === classroomCode
        );

        if (classroomExists) {
          navigate('/student', { state: { username } });
        } else {
          alert('Invalid classroom code!');
        }
      }
    } else {
      navigate('/teacher', { state: { username } });
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>{isRegistering ? 'Register as Student' : 'Login'}</h2>

      <label>
        Role:
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="student">Student</option>
          <option value="teacher">Teacher</option>
        </select>
      </label>

      <br /><br />

      <label>
        Username:
        <input
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
      </label>

      <br /><br />

      <label>
        Password:
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </label>

      <br /><br />

      {role === 'student' && (
        <>
          <label>
            Classroom Code:
            <input
              value={classroomCode}
              onChange={(e) => setClassroomCode(e.target.value)}
            />
          </label>
          <br /><br />
        </>
      )}

      <button onClick={handleLogin}>
        {isRegistering ? 'Register' : 'Log In'}
      </button>

      <br /><br />

      <button onClick={() => setIsRegistering(!isRegistering)}>
        {isRegistering ? 'Already have an account? Log in' : 'Need an account? Register'}
      </button>
    </div>
  );
}

export default LoginPage;
