import React, { useState } from 'react'; 
import { useNavigate } from 'react-router-dom';

function LoginPage() {
  const [role, setRole] = useState('student');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [classroomCode, setClassroomCode] = useState('');
  const navigate = useNavigate();

  const handleLogin = () => {
    if (!username || !password) {
      alert('Please enter a username and password.');
      return;
    }

    if (role === 'student') {
      // Validate classroom code against stored codes in localStorage
      const savedClassrooms = localStorage.getItem('classrooms');
      const classrooms = savedClassrooms ? JSON.parse(savedClassrooms) : [];

      const classroomExists = classrooms.some((classroom) => classroom.code === classroomCode);

      if (classroomExists) {
        navigate('/student', { state: { username } });
      } else {
        alert('Invalid classroom code!');
      }
    } else {
      navigate('/teacher', { state: { username } });
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Login</h2>

      <label>
        Role:
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="student">Student</option>
          <option value="teacher">Teacher</option>
        </select>
      </label>

      <br /><br />

      <label>
        Username:
        <input value={username} onChange={(e) => setUsername(e.target.value)} />
      </label>

      <br /><br />

      <label>
        Password:
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </label>

      <br /><br />

      {role === 'student' && (
        <>
          <label>
            Classroom Code:
            <input value={classroomCode} onChange={(e) => setClassroomCode(e.target.value)} />
          </label>
          <br /><br />
        </>
      )}

      <button onClick={handleLogin}>Log In</button>
    </div>
  );
}

export default LoginPage;
