import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function LoginPage() {
    const [role, setRole] = useState('student'); // Track whether the user is a teacher or student
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [studentCode, setStudentCode] = useState('');
    const [isRegistering, setIsRegistering] = useState(false); // Toggle between Login and Register
    const navigate = useNavigate();

    const handleRoleChange = (event) => {
        setRole(event.target.value);
    };

    const handleSubmit = () => {
        if (isRegistering) {
            if (!username || !password) {
                alert('Please enter both username and password.');
                return;
            }

            if (role === 'student' && !studentCode) {
                alert('Please enter your student code.');
                return;
            }

            // Register logic (saving to localStorage for now)
            const users = JSON.parse(localStorage.getItem('users')) || [];
            users.push({ username, password, role, studentCode });
            localStorage.setItem('users', JSON.stringify(users));

            alert(`${role.charAt(0).toUpperCase() + role.slice(1)} registered successfully!`);
            navigate(`/${role}`, { state: { username } });
        } else {
            // Login logic
            const users = JSON.parse(localStorage.getItem('users')) || [];
            const user = users.find(u => u.username === username && u.password === password && u.role === role);

            if (user) {
                navigate(`/${role}`, { state: { username } });
            } else {
                alert('Invalid credentials!');
            }
        }
    };

    return (
        <div style={{ padding: '2rem' }}>
            <h2>{isRegistering ? `Register as ${role.charAt(0).toUpperCase() + role.slice(1)}` : 'Login'}</h2>
            <div>
                <label>
                    <input
                        type="radio"
                        name="role"
                        value="student"
                        checked={role === 'student'}
                        onChange={handleRoleChange}
                    />
                    Student
                </label>
                <label>
                    <input
                        type="radio"
                        name="role"
                        value="teacher"
                        checked={role === 'teacher'}
                        onChange={handleRoleChange}
                    />
                    Teacher
                </label>
            </div>
            <br />
            <label>
                Username:
                <input
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
            </label>
            <br />
            <label>
                Password:
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
            </label>
            <br />
            {role === 'student' && isRegistering && (
                <label>
                    Student Code:
                    <input
                        type="text"
                        value={studentCode}
                        onChange={(e) => setStudentCode(e.target.value)}
                        required={role === 'student' && isRegistering}
                    />
                </label>
            )}
            <br />
            <button onClick={handleSubmit}>
                {isRegistering ? 'Register' : 'Log In'}
            </button>
            <br />
            <button onClick={() => setIsRegistering(!isRegistering)}>
                {isRegistering ? 'Already have an account? Log in' : 'Need an account? Register'}
            </button>
        </div>
    );
}

export default LoginPage;
