import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

function ClassroomMenuPage() {
    const location = useLocation();
    const { classrooms } = location.state || {};
    const classroomCode = location.pathname.split('/').pop();  // Get the classroom code from the URL

    const [sortOption, setSortOption] = useState('alphabetically');

    if (!classrooms) {
        return <div>No classrooms available.</div>;
    }

    const classroom = classrooms.find(c => c.code === classroomCode);
    if (!classroom) {
        return <div>Classroom not found.</div>;
    }

    // Sort students alphabetically
    const sortedStudentsAlphabetically = [...classroom.students].sort((a, b) =>
        a.username.localeCompare(b.username)
    );

    // Group students by major
    const studentsByMajor = classroom.students.reduce((acc, student) => {
        if (!acc[student.major]) {
            acc[student.major] = [];
        }
        acc[student.major].push(student);
        return acc;
    }, {});

    const handleSortChange = (option) => {
        setSortOption(option);
    };

    return (
        <div style={{ padding: '2rem' }}>
            <h2>{classroom.name} ({classroom.code})</h2>

            <button onClick={() => handleSortChange('alphabetically')}>Sort Alphabetically</button>
            <button onClick={() => handleSortChange('byMajor')}>Sort by Major</button>

            <h3>Students</h3>
            {sortOption === 'alphabetically' ? (
                <ul>
                    {sortedStudentsAlphabetically.map((student, index) => (
                        <li key={index}>{student.username} ({student.major})</li>
                    ))}
                </ul>
            ) : (
                Object.keys(studentsByMajor).map((major) => (
                    <div key={major}>
                        <h4>{major}</h4>
                        <ul>
                            {studentsByMajor[major].map((student, i) => (
                                <li key={i}>{student.username} ({student.major})</li>
                            ))}
                        </ul>
                    </div>
                ))
            )}
        </div>
    );
}

export default ClassroomMenuPage;
