import React, { useState } from 'react'; 
import { useLocation, useNavigate } from 'react-router-dom';

function TeacherMenuPage() {
    const location = useLocation();
    const navigate = useNavigate();
    const username = location.state?.username || 'Teacher';

    const [classroomCode, setClassroomCode] = useState('');
    const [classroomName, setClassroomName] = useState('');
    const [classrooms, setClassrooms] = useState(() => {
        // Retrieve any saved classrooms from localStorage
        const savedClassrooms = localStorage.getItem('classrooms');
        return savedClassrooms ? JSON.parse(savedClassrooms) : [];
    });

    const handleCreateClassroom = () => {
        if (classroomCode && classroomName) {
            // Add the new classroom to the list
            const newClassroom = { code: classroomCode, name: classroomName };
            const updatedClassrooms = [...classrooms, newClassroom];

            // Save the new list of classrooms to localStorage
            localStorage.setItem('classrooms', JSON.stringify(updatedClassrooms));

            // Update the state and clear input fields
            setClassrooms(updatedClassrooms);
            setClassroomCode('');
            setClassroomName('');
            alert('Classroom created successfully!');
        } else {
            alert('Please enter both classroom code and name!');
        }
    };

    const handleLogout = () => {
        navigate('/'); // Log out and go back to login page
    };

    return (
        <div style={{ padding: '2rem' }}>
            <h2>Welcome, {username} (Teacher)</h2>
            <h3>Create Classroom</h3>
            <input
                type="text"
                placeholder="Classroom Code"
                value={classroomCode}
                onChange={(e) => setClassroomCode(e.target.value)}
            />
            <br />
            <input
                type="text"
                placeholder="Classroom Name"
                value={classroomName}
                onChange={(e) => setClassroomName(e.target.value)}
            />
            <br />
            <button onClick={handleCreateClassroom}>Create Classroom</button>
            <br /><br />
            <button onClick={handleLogout}>Log Out</button>
        </div>
    );
}

export default TeacherMenuPage;
